configuration.config:
package Naming is
   for Body_Suffix ("Asm_Cpp") use ".S";
end Naming;

package Compiler is
   for Driver            ("Asm_Cpp") use "${PATH}${PREFIX}gcc";
   for Required_Switches ("Asm_Cpp") use ("-c", "-x", "assembler-with-cpp");
   for Include_Path      ("Asm_Cpp") use "CPATH";
end Compiler;

package Naming is
   for Body_Suffix ("Asm") use ".s";
end Naming;

package Compiler is
   for Driver            ("Asm") use "${PATH}${PREFIX}gcc";
   for Required_Switches ("Asm") use ("-c", "-x", "assembler");
   for Include_Path      ("Asm") use "CPATH";
end Compiler;

package Naming is
   for Body_Suffix ("Asm2") use ".asm";
end Naming;

package Compiler is
   for Driver            ("Asm2") use "${PATH}${PREFIX}gcc";
   for Required_Switches ("Asm2") use ("-c", "-x", "assembler");
   for Include_Path      ("Asm2") use "CPATH";
end Compiler;
