configuration.config:
package Naming is
   for Spec_Suffix ("C") use ".h";
   for Body_Suffix ("C") use ".c";
end Naming;

package Compiler is
   for Driver              ("C") use "${PATH}${PREFIX}gcc";
end Compiler;

package Compiler is
   for Driver              ("C") use "${PATH}gcc${PREFIX}";
end Compiler;

package Compiler is
   for Driver              ("C") use "${PATH}cc${PREFIX}";
end Compiler;

package Compiler is
   for Driver              ("C") use "${PATH}dcc";
end Compiler;

package Compiler is
   for Required_Switches   ("C") use
      Compiler'Required_Switches ("C") & ("-c", "-x", "c");
   for Dependency_Switches ("C") use ("-Wp,-MMD,");
   for Include_Path        ("C") use "CPATH";
end Compiler;

package Compiler is
   for Required_Switches   ("C") use
      Compiler'Required_Switches ("C") & ("-tPPC750EH:cross", "-c");
   for Dependency_Switches ("C") use
      ("-Xmake-dependency=6", "-Xmake-dependency-savefile=");
   for Include_Path        ("C") use "CPATH";
end Compiler;

package Compiler is
   for PIC_Option ("C") use ("-fPIC");
end Compiler;

package Compiler is
   for Required_Switches ("C") use
     Compiler'Required_Switches ("C") & ("-mno-cygwin");
end Compiler;
