configuration.config:
package Naming is
   for Spec_Suffix ("C") use ".h";
   for Body_Suffix ("C") use ".c";
end Naming;

package Language_Processing is
   for Language_Kind            ("C") use "file_based";
   for Compiler_Driver          ("C") use "gcc";
   for Compiler_Minimum_Options ("C") use ("-c", "-x", "c");
   for Dependency_File_Kind     ("C") use "Makefile";
   for Dependency_Option        ("C") use ("-Wp,-MD,");
   for Include_Path             ("C") use "CPATH";
end Language_Processing;

package Language_Processing is
   for Compiler_PIC_Option      ("C") use ("-fPIC");
end Language_Processing;
