configuration.config:
package Naming is
   for Spec_Suffix ("C++") use ".hh";
   for Body_Suffix ("C++") use ".cpp";
end Naming;

package Language_Processing is
   for Language_Kind            ("C++") use "file_based";
   for Compiler_Driver          ("C++") use "g++";
   for Minimum_Compiler_Options ("C++") use ("-c", "-x", "c++");
   for Dependency_File_Kind     ("C++") use "Makefile";
   for Dependency_Option        ("C++") use ("-Wp,-MD,");
   for Include_Path             ("C++") use "CPATH";
end Language_Processing;

package Language_Processing is
   for Compiler_PIC_Option      ("C++") use ("-fPIC");
end Language_Processing;

package Language_Processing is
   for Minimum_Compiler_Options ("C++") use
     Language_Processing'Minimum_Compiler_Options ("C++") & ("-mno-cygwin");
   for Default_Minimum_Linker_Options use
     Language_Processing'Minimum_Linker_Options & ("-mno-cygwin");
end Language_Processing;
