configuration.config:
package Compiler is
   for Driver              ("C++") use "${PATH(c++)}${PREFIX(c++)}g++";
end Compiler;

package Compiler is
   for Driver              ("C++") use "${PATH(c++)}c++${PREFIX(c++)}";
end Compiler;

package Compiler is
   for Driver              ("C++") use "${PATH(c++)}dcc";
end Compiler;

package Compiler is
   for Driver              ("C++") use "${PATH(c++)}clang++";
end Compiler;

package Compiler is
   for Leading_Required_Switches   ("C++") use
     ("-c", "-x", "c++") &
     Compiler'Leading_Required_Switches ("C++");
   for Dependency_Switches ("C++") use ("-MMD", "-MF", "");
   for Include_Switches_Via_Spec ("C++") use ("cc1plus", "-I");
   for Max_Command_Line_Length use "8192";
   for Response_File_Format    ("C++") use "GCC_GNU";
end Compiler;

package Compiler is
   for Leading_Required_Switches   ("C++") use
     ("-c", "-x", "c++") &
     Compiler'Leading_Required_Switches ("C++");
   for Dependency_Switches ("C++") use ("-MMD", "-MF", "");
   for Max_Command_Line_Length use "8192";
   for Response_File_Format    ("C++") use "GCC_GNU";
end Compiler;

package Compiler is
   for Leading_Required_Switches   ("C++") use
     Compiler'Leading_Required_Switches ("C++") &
     ("-c", "-x", "c++");
   for Dependency_Switches ("C++") use ("-Wp,-MMD,");
   for Include_Path        ("C++") use "CPATH";
end Compiler;

package Compiler is
   for PIC_Option      ("C++") use ("-fPIC");
end Compiler;

package Compiler is
   for Leading_Required_Switches ("C++") use
      Compiler'Leading_Required_Switches ("C++") & ("-mno-cygwin");
end Compiler;

package Compiler is
   for Leading_Required_Switches ("C++") use
     Compiler'Leading_Required_Switches ("C++") & ("-mrtp");
end Compiler;
