configuration.config:
package Naming is
      for Spec_Suffix ("C++") use ".hh";
      for Body_Suffix ("C++") use ".cpp";
   end Naming;

   package Compiler is
      for Driver              ("C++") use "g++";
      for Switches            ("C++") use ("-c", "-x", "c++");
      for Dependency_Switches ("C++") use ("-Wp,-MD,");
      for Include_Path        ("C++") use "CPATH";
   end Compiler;

package Compiler is
   for PIC_Option      ("C++") use ("-fPIC");
end Compiler;

package Compiler is
   for Switches            ("C++") use Compiler'Switches ("C++") & ("-mno-cygwin");
end Compiler;
