configuration.config:
package Builder is
   for Executable_Suffix use "";
end Builder;

package Builder is
   for Executable_Suffix use ".out";
end Builder;

for Archive_Builder use ("arppc", "cr");
for Archive_Indexer use ("ranlibppc");
for Archive_Suffix  use ".a";

package Builder is
   for Executable_Suffix use ".vxe";
end Builder;

package Compiler is
   for Required_Switches ("Ada") use
     Compiler'Required_Switches ("Ada") & ("-mrtp", "--RTS=$RUNTIME");
end Compiler;

package Binder is
   for Required_Switches ("Ada") use
     Binder'Required_Switches ("Ada") & ("--RTS=$RUNTIME");
end Binder;

package Compiler is
   for Required_Switches ("C") use
     Compiler'Required_Switches ("C") & ("-mrtp")
     & ("-I" & Wind_Base & "/target/usr/h");
end Compiler;

package Compiler is
   for Required_Switches ("C") use
     Compiler'Required_Switches ("C")
     & ("-I" & Wind_Base & "/target/h");
end Compiler;

package Builder is
   for Executable_Suffix use ".out";
end Builder;

for Archive_Builder  use ("arppc", "cr");
for Archive_Indexer  use ("ranlibppc");
for Archive_Suffix   use ".a";

for Archive_Builder  use ("arpentium", "cr");
for Archive_Indexer  use ("ranlibpentium");
for Archive_Suffix   use ".a";

