configuration.config:
package Builder is
   for Executable_Suffix use "";
end Builder;

package Builder is
   for Executable_Suffix use ".out";
end Builder;

package Builder is
   for Executable_Suffix use ".vxe";
end Builder;

package Compiler is
   for Switches ("Ada") use
     Binder'Switches ("Ada") & ("-mrtp", "--RTS=$RUNTIME");
end Compiler;

package Binder is
   for Switches ("Ada") use
     Binder'Switches ("Ada") & ("--RTS=$RUNTIME");
end Binder;
