configuration.config:
package Language_Processing is
   for Executable_Suffix use "";
end Language_Processing;

package Language_Processing is
   for Executable_Suffix use ".out";
end Language_Processing;

package Language_Processing is
   for Executable_Suffix use ".vxe";
   for Minimum_Compiler_Options ("Ada") use
     Language_Processing'Minimum_Compiler_Options ("Ada") & ("-mrtp", "--RTS=$RUNTIME");
   for Minimum_Binder_Options ("Ada") use
     Language_Processing'Minimum_Binder_Options ("Ada") & ("--RTS=$RUNTIME");
end Language_Processing;
