configuration.config:
package Naming is
   for Body_Suffix ("Fortran") use ".f";
end Naming;

package Language_Processing is
   for Language_Kind            ("Fortran") use "file_based";
   for Compiler_Driver          ("Fortran") use "g77";
   for Minimum_Compiler_Options ("Fortran") use ("-c");
   for Dependency_File_Kind     ("Fortran") use "Makefile";
   for Dependency_Option        ("Fortran") use ("-Wp,-MD,");
   for Include_Path             ("Fortran") use "CPATH";
end Language_Processing;

package Language_Processing is
   for Compiler_PIC_Option      ("Fortran") use ("-fPIC");
end Language_Processing;

package Language_Processing is
   for Minimum_Compiler_Options ("Fortran") use
     Language_Processing'Minimum_Compiler_Options ("Fortran") & ("-mno-cygwin");
end Language_Processing;
