configuration.config:
package Naming is
   for Body_Suffix ("Fortran") use ".f";
end Naming;

package Compiler is
   for Driver          ("Fortran") use "g77";
   for Switches        ("Fortran") use ("-c", "-fno-underscoring");
   for Include_Path    ("Fortran") use "CPATH";
end Compiler;

package Compiler is
   for PIC_Option      ("Fortran") use ("-fPIC");
end Compiler;

package Compiler is
   for Switches ("Fortran") use
     Compiler'Switches ("Fortran") & ("-mno-cygwin");
end Compiler;

package Linker is
   for Switches ("Fortran") use
      Linker'Switches ("Fortran")  & ("-mno-cygwin");
end Linker;
