configuration.config:
package Naming is
   for Spec_Suffix ("Ada") use ".ads";
   for Body_Suffix ("Ada") use ".adb";
   for Casing use "lowercase";
   for Dot_Replacement use "-";
end Naming;

package Compiler is
   for Driver                   ("Ada") use "gnaamp";
   for Required_Switches        ("Ada") use
       Compiler'Required_Switches ("Ada")
          & ("-gnatA");
   for Object_File_Suffix       ("Ada") use ".obj";
   for Mapping_File_Switches    ("Ada") use ("-gnatem=");
   for Mapping_Spec_Suffix      ("Ada") use "%s";
   for Mapping_Body_Suffix      ("Ada") use "%b";
   for Config_File_Switches     ("Ada") use ("-gnatec=");
   for Include_Path_File     ("Ada") use "ADA_PRJ_INCLUDE_FILE";
   for Config_Body_File_Name    ("Ada") use
        "pragma Source_File_Name_Project (%u, Body_File_Name => ""%f"");";
   for Config_Spec_File_Name    ("Ada") use
        "pragma Source_File_Name_Project (%u, Spec_File_Name => ""%f"");";
   for Config_Body_File_Name_Pattern ("Ada") use
        "pragma Source_File_Name_Project " &
        "  (Body_File_Name  => ""*%b""," &
        "   Casing          => %c," &
        "   Dot_Replacement => ""%d"");";
   for Config_Spec_File_Name_Pattern ("Ada") use
        "pragma Source_File_Name_Project " &
        "  (Spec_File_Name  => ""*%s""," &
        "   Casing          => %c," &
        "   Dot_Replacement => ""%d"");";
   for Config_File_Unique    ("Ada") use "False";
end Compiler;

package Binder is
   for Objects_Path_File     ("Ada") use "ADA_PRJ_OBJECTS_FILE";
   for Required_Switches     ("Ada") use
       Binder'Required_Switches ("Ada")  &
       ("-I-", "ada_binder=gnaampbind");
   for Driver         ("Ada") use
       "${GPRCONFIG_PREFIX}/libexec/gprbuild/gprbind";
end Binder;

for Toolchain_Version     ("Ada") use "GNAT ${VERSION(ada)}";
for Runtime_Library_Dir   ("Ada") use "${RUNTIME_DIR(ada)}";
for Runtime_Source_Dir    ("Ada") use "${RUNTIME_DIR(ada)}/../adainclude";

gprconfig.configuration:
   &filter_gnat;

   package Naming is
      for Spec_Suffix ("Ada") use ".ads";
      for Body_Suffix ("Ada") use ".adb";
      for Casing use "lowercase";
      for Dot_Replacement use "-";
   end Naming;

   package Compiler is
      for Driver                   ("Ada") use "${PATH(ada)}${PREFIX(ada)}gcc";
      for Leading_Required_Switches        ("Ada") use
          Compiler'Leading_Required_Switches ("Ada")
	     & ("-c", "-x", "ada", "-gnatA");
      for Mapping_File_Switches    ("Ada") use ("-gnatem=");
      for Mapping_Spec_Suffix      ("Ada") use "%s";
      for Mapping_Body_Suffix      ("Ada") use "%b";
      for Config_File_Switches     ("Ada") use ("-gnatec=");
      for Include_Path_File     ("Ada") use "ADA_PRJ_INCLUDE_FILE";
   end Compiler;

   package Binder is
      for Objects_Path_File     ("Ada") use "ADA_PRJ_OBJECTS_FILE";
      for Required_Switches     ("Ada") use
          Binder'Required_Switches ("Ada")  & ("-I-");
      for Driver         ("Ada") use
          "${GPRCONFIG_PREFIX}libexec/gprbuild/gprbind";
   end Binder;

   for Toolchain_Version     ("Ada") use "GNAT ${VERSION(ada)}";
   for Runtime_Library_Dir   ("Ada") use "${RUNTIME_DIR(ada)}";
   for Runtime_Source_Dir    ("Ada") use "${RUNTIME_DIR(ada)}/../adainclude";

&filter_gnat;







package Compiler is
   for Multi_Unit_Switches ("Ada") use ("-gnateI");
   for Multi_Unit_Object_Separator ("Ada") use "~";
   for Config_Body_File_Name ("Ada") use
        "pragma Source_File_Name_Project (%u, Body_File_Name => ""%f"");";
   for Config_Spec_File_Name ("Ada") use
        "pragma Source_File_Name_Project (%u, Spec_File_Name => ""%f"");";
   for Config_Body_File_Name_Index ("Ada") use
        "pragma Source_File_Name_Project (%u, Body_File_Name => ""%f"", Index => %i);";
   for Config_Spec_File_Name_Index ("Ada") use
        "pragma Source_File_Name_Project (%u, Spec_File_Name => ""%f"", Index => %i);";
   for Config_Body_File_Name_Pattern ("Ada") use
        "pragma Source_File_Name_Project " &
        "  (Body_File_Name  => ""*%b""," &
        "   Casing          => %c," &
        "   Dot_Replacement => ""%d"");";
   for Config_Spec_File_Name_Pattern ("Ada") use
        "pragma Source_File_Name_Project " &
        "  (Spec_File_Name  => ""*%s""," &
        "   Casing          => %c," &
        "   Dot_Replacement => ""%d"");";
   for Config_File_Unique    ("Ada") use "False";
end Compiler;

&filter_gnat;





