configuration.config:
package Compiler is
   for Driver ("RC") use "${PREFIX(RC)}windres";
   for Leading_Required_Switches ("RC") use ("-i");
   for Object_File_Suffix ("RC") use ".coff";
   for Object_File_Switches ("RC") use ("-o", "");
end Compiler;

package Naming is
   for Body_Suffix ("RC") use ".rc";
end Naming;
